(()=>{window.addEventListener("load",function(){const s=document.querySelector(".awesomplete-combo-group"),e=s.querySelector("input"),o=s.querySelector(".button-trigger"),i=s.querySelector(".button-clear"),n=document.querySelectorAll("a[data-topics]"),t=new Awesomplete(e,{minChars:0,sort:!1,replace:function(e){this.input.value=e.label}});Awesomplete.$(o).addEventListener("click",function(){t.ul.childNodes.length===0?(t.evaluate(),e.focus()):t.ul.hasAttribute("hidden")?(t.open(),e.focus()):t.close()}),Awesomplete.$(i).addEventListener("click",()=>{e.value="",t.evaluate(),t.close(),n.forEach(e=>e.style.display="block")}),e.addEventListener("awesomplete-selectcomplete",e=>{const t=e.text.value;n.forEach(e=>e.style.display="block"),[...n].filter(e=>!e.dataset.topics.includes(t)).forEach(e=>e.style.display="none")}),e.addEventListener("awesomplete-close",t=>{(t.reason==="blur"||t.reason==="esc")&&e.value===""&&n.forEach(e=>e.style.display="block")})})})()