(()=>{window.addEventListener("load",function(){const t=document.querySelector(".awesomplete-combo-group"),n=t.querySelector("input"),o=t.querySelector(".button-trigger"),i=t.querySelector(".button-clear"),s=document.querySelectorAll("a[data-topics]"),e=new Awesomplete(n,{minChars:0,sort:!1,replace:function(e){this.input.value=e.label}});Awesomplete.$(o).addEventListener("click",function(){e.ul.childNodes.length===0?e.evaluate():e.ul.hasAttribute("hidden")?e.open():e.close()}),Awesomplete.$(i).addEventListener("click",function(){n.value="",s.forEach(e=>e.style.display="block")}),n.addEventListener("awesomplete-selectcomplete",function(e){const t=e.text.value;s.forEach(e=>e.style.display="block"),t!=="clear-topic-filter"&&[...s].filter(e=>!e.dataset.topics.includes(t)).forEach(e=>e.style.display="none")})})})()