(()=>{window.addEventListener("load",function(){const s=document.querySelector(".awesomplete-combo-group"),t=s.querySelector("input"),o=s.querySelector(".button-trigger"),i=s.querySelector(".button-clear"),n=document.querySelectorAll("a[data-topics]"),e=new Awesomplete(t,{minChars:0,sort:!1,replace:function(e){this.input.value=e.label}});Awesomplete.$(o).addEventListener("click",function(){e.ul.childNodes.length===0?e.evaluate():e.ul.hasAttribute("hidden")?e.open():e.close()}),Awesomplete.$(i).addEventListener("click",()=>{t.value="",e.evaluate(),e.close(),n.forEach(e=>e.style.display="block")}),t.addEventListener("awesomplete-selectcomplete",e=>{const t=e.text.value;n.forEach(e=>e.style.display="block"),[...n].filter(e=>!e.dataset.topics.includes(t)).forEach(e=>e.style.display="none")}),t.addEventListener("awesomplete-close",e=>{(e.reason==="blur"||e.reason==="esc")&&t.value===""&&n.forEach(e=>e.style.display="block")})})})()